/*
 
 Write a Program to declare and initialize all the data types in Java
 
 */

package basicsprogram;

public class Declare_Initialize 

{

	public static void main(String[] args) 
	
	{
		// TODO Auto-generated method stub
		
		byte b1=81;		//declaration & initialization
		short s1=8762;  //declaration & initialization
		int i1=857421;  //declaration & initialization
		long l1=745874521;   //declaration & initialization
		
		float f1=32.74f;     //declaration & initialization
		double d1=74532.32;  //declaration & initialization
		
		char c1='A';         //declaration & initialization
		char gender='M';     //declaration & initialization
		
		boolean b2=true;     //declaration & initialization
		
		String z1="MKT Class"; //declaration & initialization
		
		System.out.println(b1);
		System.out.println(s1);
		System.out.println(i1);
		System.out.println(l1);
		
		System.out.println(f1);
		System.out.println(d1);
		
		System.out.println(c1);
		System.out.println(gender);
		
		System.out.println(b2);
		
		System.out.println(z1);
	}

}
